#!/bin/sh
ulimit -c unlimited && ulimit -a && \rm -rf $HOME/var/data/* && env ASAN_SYMBOLIZER_PATH="/usr/local/llvm-devel/bin/llvm-symbolizer" env ASAN_OPTIONS="detect_stack_use_after_return=1" python buildscripts/resmoke.py --suites=concurrency_sharded --basePort=10000 --storageEngine=wiredTiger --excludeWithAnyTags=requires_mmapv1 --dbpathPrefix=$HOME/var/data --repeat=5000 --continueOnFailure | tee resmoke.logs/`date +A0%Y%m%d%H%M%S` 2>&1
